"use strict";function openMenu(){$(".atena__header-container--menu-button, .atena__close-menu, .atena__overlay").on("click",function(){$(".atena__mobile-menu, .atena__close-menu, .atena__overlay").toggleClass("is--active")})}function menuColorChange(){$(window).on("scroll",function(){$(".atena__header").offset().top>0?$(".atena__header").addClass("has--scrolled"):$(".atena__header").removeClass("has--scrolled")})}document.addEventListener("DOMContentLoaded",function(){openMenu(),menuColorChange()});